mapper.update:
update auth_role_user
set user_id = #{userId,jdbcType=CHAR},
  role_id = #{roleId,jdbcType=CHAR},
  status = #{status,jdbcType=INTEGER}
where user_role_id = #{userRoleId,jdbcType=CHAR}

mapper.select:
select user_role_id, user_id, role_id, status
from auth_role_user
where user_role_id = #{userRoleId,jdbcType=CHAR}
